use customer;
-- Total revenue generated by male vs. female customers
select gender,sum(purchase_amount) as revenue from customer_data
group by gender;

-- Top 5 products with the highest average review rating
select item_purchased,round(avg(review_rating),2) as highest_rating from
customer_data
group by item_purchased
order by avg(review_rating) desc
limit 5;
-- Average Purchase Amounts between Standard and Express Shipping
select shipping_type,round(avg(purchase_amount),2) as avgpurchaseamount
from customer_data
where shipping_type in('Standard','Express')
group by shipping_type;
--  Do subscribed customers spend more?Average spend and total revenue between subscribers and non-subscribers.
SELECT subscription_status,
       COUNT(customer_id) AS total_customers,
       ROUND(AVG(purchase_amount),2) AS avg_spend,
       ROUND(SUM(purchase_amount),2) AS total_revenue
FROM customer_data
GROUP BY subscription_status
ORDER BY total_revenue,avg_spend DESC;

-- 5 productsthat  have the highest percentage of purchases with discounts applied
SELECT item_purchased,
       ROUND(100.0 * SUM(CASE WHEN discount_applied = 'Yes' THEN 1 ELSE 0 END)/COUNT(*),2) AS discount_rate
FROM customer_data
GROUP BY item_purchased
ORDER BY discount_rate DESC
LIMIT 5;

-- Segment customers into New, Returning, and Loyal based on their total number of previous purchases, and show the count of each segment. 
with customer_type as (
SELECT customer_id, previous_purchases,
CASE 
    WHEN previous_purchases = 1 THEN 'New'
    WHEN previous_purchases BETWEEN 2 AND 10 THEN 'Returning'
    ELSE 'Loyal'
    END AS customer_segment
FROM customer_data)
select customer_segment,count(*) AS "Number of Customers" 
from customer_type 
group by customer_segment;

-- Top 3 most purchased products within each category
WITH item_counts AS (
    SELECT category,
           item_purchased,
           COUNT(customer_id) AS total_orders,
           ROW_NUMBER() OVER (PARTITION BY category ORDER BY COUNT(customer_id) DESC) AS item_rank
    FROM customer_data
    GROUP BY category, item_purchased
)
SELECT item_rank,category, item_purchased, total_orders
FROM item_counts
WHERE item_rank <=3;

 -- revenue contribution of each age group
SELECT 
    age_group,
    SUM(purchase_amount) AS total_revenue
FROM customer_data
GROUP BY age_group
ORDER BY total_revenue desc;

 -- sales of each age group
SELECT 
    age_group,
    COUNT(*) AS total_sales
FROM customer_data
GROUP BY age_group
ORDER BY total_sales DESC;

 -- sales of each category
SELECT 
    category,
    COUNT(*) AS total_sales
FROM customer_data
GROUP BY category
ORDER BY total_sales DESC;




